mapper.select:
select user_id,flower_num from treasure_user where user_pin in
#{userPin}
and yn = 1

mapper.update:
update treasure_user
set
flower_num = flower_num + #{addNum}
where user_pin = #{userPin}
    AND
    flower_num = #{flowerNum}
    AND
    status = 1
    AND
    yn = 1

update.foreach:
update treasure_user
set nickname = #{item.nickname}
where user_id = #{item.userId}     and       yn = 1
